INSERT INTO `la_articles` VALUES (60, 0, 9, 1, '中国', '北京市', '佑安中心', 350000000, '写字楼', '其他', NULL, '北京市丰台区右安门外福宜街，西铁营万达广场东侧', 39230.00, NULL, '在建', NULL, '地处二环沿线，被丽泽，金融街，西单和总部基地四大商务区环绕，区位得天独厚。距离14号线西铁营站（在建）仅约400米；3公里黄金商圈覆盖，1公里范围内，与北京三环内唯一一座万达广场临界对望；距离北京南站直线距离约2公里。', 0, NULL, NULL, NULL, NULL, '每栋建筑面积约1800-3500㎡/栋', '资产转让', '每楼各两部', '100左右', NULL, NULL, '首层5米，2-4层4.2米，地下一层6.4米', NULL, NULL, '剩4号楼3500平米', '容积率3.5', NULL, '24元/月/㎡', NULL, '右安门', NULL, '<p><img width=\"530\" height=\"340\" src=\"https://api.map.baidu.com/staticimage?s=1&center=116.370648,39.872264&zoom=15&width=530&height=340&markers=116.370648,39.872264\"/></p>', NULL, 2340, 2341, 0, '2020-06-18 18:05:55', '2020-06-18 18:05:55', '1', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2020-06-18 18:05:55', NULL, 0);
INSERT INTO `la_articles` VALUES (61, 0, 9, 1, '中国', '北京市', '金茂中心', 0, '商业', '其他', NULL, '朝阳区金盏金融商务区，东坝大街民航清算中心南侧', 113000.00, NULL, '在建', NULL, '朝阳区金茂中心定位为生态低密绿色的亿级企业花园独栋。建筑设计精工匠造，将钻石造型设计理念延续到整个项目，未来计划将取得美国LEED金级认证，是中国金茂精心打造的商务生态办公旗舰产品。项目中央景观达万余平方米，中央景观整体下沉6米，采用现代简约的设计风格，简约的风格在建筑外立面相得益彰。朝阳区最后的价值洼地，中心城区仅存大规模，高规格开发区域；第四使馆区，跨国企业新商圈，四横五纵，快速链接世界的交通路网；约450米直达地铁站，距离首都国际机场T3航站楼约10公里；10.5米挑高大堂，开启上午空间新纪元；5A只能化办公系统。', 0, NULL, NULL, NULL, NULL, '6栋，建筑面积约7000-15000㎡/栋', '资产转让', '3/4号楼每栋4部，5号楼5部，6号楼6部', '约570个', NULL, NULL, '10.5米挑高大堂', NULL, NULL, '3/4/5/6号楼在售', '2.0', NULL, '31元/月/㎡', NULL, '金盏金融商务区', NULL, '<p><img width=\"530\" height=\"340\" src=\"https://api.map.baidu.com/staticimage?s=1&center=116.459599,39.927437&zoom=12&width=530&height=340&markers=116.459599,39.927437\"/></p>', NULL, 2338, 2339, 0, '2020-06-18 15:58:31', '2020-06-18 15:58:31', '1', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2020-06-18 15:58:31', NULL, 0);
INSERT INTO `la_articles` VALUES (62, 0, 9, 1, '中国', '北京市', '未来金茂府商办', 0, '商业', NULL, NULL, '北五环未来科学城南区英才南三街与蓬莱苑南街交叉口', 53500.00, NULL, NULL, NULL, '未来金茂府位于北五环未来科学城南区英才南三街与蓬莱苑南街交叉口，作为金茂洋房府系产品——北京第5座金茂府。央企科技创新区，多元商务综合体。城市更迭焕新，不负时代激扬。作为全球科创高地、北京“三城一区”科技战略主场， 凭借政策红利与产业优势，未来科学城正迅速崛起，成为北京甚至全国科学研发的新高地。大规模高 精尖智慧人群和产业资本的进驻所集聚的财智人群，都对环境提出更好的需求。', 3, NULL, NULL, NULL, NULL, '共三栋', '资产转让', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '建筑面积约3393-25000㎡/栋', NULL, NULL, NULL, NULL, '未来科学城', NULL, '<p><img width=\"530\" height=\"340\" src=\"https://api.map.baidu.com/staticimage?s=1&center=116.474251,40.130696&zoom=14&width=530&height=340&markers=116.474251,40.130696\"/></p>', NULL, 2336, 2337, 1, '2020-06-18 15:57:37', '2020-06-18 15:57:37', '1', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2020-06-18 15:57:37', NULL, 0);
